#!/usr/bin/python # coding=utf-8 import pygame
from src.game1.util import ImgUtil import math
class hero():
def __init__(self, x_position, y_position, length, height): self.x = x_position self.y = y_position self.l = length self.h = height self.player_ship = pygame.transform.scale(ships[0], (128, 128)) self.angle = 0
def draw(self, win):
cx, cy = pygame.mouse.get_pos() dx, dy = cx - self.x, cy - self.y if abs(dx) > 0 or abs(dy) > 0: self.angle = math.atan2(-dx, -dy)*57.2957795
img_copy = pygame.transform.rotate(self.player_ship, self.angle) rotated_rect = img_copy.get_rect(center = (round(self.x), round(self.y)))
win.blit(img_copy, rotated_rect)
def move(self, speed): cx, cy = pygame.mouse.get_pos() dx, dy = cx - self.x, cy - self.y if abs(dx) > 0 or abs(dy) > 0: dist = math.hypot(dx, dy) self.x += min(dist, speed) * dx/dist self.y += min(dist, speed) * dy/dist

class Bullet:
    def __init__(self, x, y, angle):
        self.x = x
        self.y = y
        self.angle = angle+90

    def update(self):
        speed = 10  # 子弹速度

        self.x += math.cos(math.radians(self.angle)) * speed
        self.y += math.sin(math.radians(self.angle)) * speed

    def draw(self, win):

        pygame.draw.circle(win, (255, 0, 0), (round(self.x), round(self.y)), 5)  能不能给子弹方向设置为当前人物的朝向

def add_skill(self, item):
    self.skill.append(item)
    if len(self.skill) > 2:
        self.skill = self.skill[-2:] 这个改成从前往后插入数据
